import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { docautService } from '../docaut.service';

@Component({
  selector: 'app-doclogin',
  templateUrl: './doc-login.component.html',
  styleUrl: './doc-login.component.css'
})
export class DocloginComponent {

   username:string='';
   password:string='';

   inValidLogin=false;

   constructor(private router:Router,private docaut:docautService){}

   checkLogin(){

             if(this.docaut.authenticate(this.username,this.password)){

                this.router.navigate(['docdash'])
                this.inValidLogin=false

             }
             else{
                     this.inValidLogin=true
                      alert("Wrong Credintials")
                      this.router.navigate(['home'])

             }
   }
}
