import { Component } from '@angular/core';
import { NgForm } from '@angular/forms'; // Add this
import { Appointment } from '../appointment';
import { Router } from '@angular/router';
import { AppointmentService } from '../appointment.service';

@Component({
  selector: 'app-create-appointment',
  templateUrl: './create-appointment.component.html',
  styleUrls: ['./create-appointment.component.css']
})
export class CreateAppointmentComponent {

  appointment: Appointment = new Appointment();

  constructor(
    private appointmentService: AppointmentService,
    private router: Router
  ) {}

  // Called on form submit
  onSubmit(form: NgForm) {
    if (form.invalid) {
      // alert('Please fill all required fields!');
      return; 
    }

    this.saveAppointment(form);
  }

  // Save appointment to backend
  saveAppointment(form: NgForm) {
    this.appointmentService.createAppointment(this.appointment)
      .subscribe({
        next: (data) => {
          console.log('Saved appointment:', data);
          form.resetForm();      // Reset form after save
          this.goTOAppointment(); // Navigate to list
        },
        error: (err) => {
          console.error('Error saving appointment:', err);
          alert('Failed to save appointment. Please try again.');
        }
      });
  }

  // Navigate to appointment list
  goTOAppointment() {
    this.router.navigate(['/appointmentlist']);
  }

  testClick() {
    alert('Button click works');
  }
}
<div class="container">
  <h2 class="text-center">Add New Appointment</h2>

  <form #f="ngForm" (ngSubmit)="onSubmit(f)" novalidate>

    <!-- Name -->
    <div class="form-group">
      <label for="name">Name:</label>
      <input
        type="text"
        id="name"
        name="name"
        class="form-control"
        placeholder="Enter name"
        required
        [(ngModel)]="appointment.name">
      <div *ngIf="f.submitted && !appointment.name" class="text-danger">
        Name is required.
      </div>
    </div>

    <!-- Age -->
    <div class="form-group">
      <label for="age">Age:</label>
      <input
        type="number"
        id="age"
        name="age"
        class="form-control"
        placeholder="Enter age"
        required
        [(ngModel)]="appointment.age">
      <div *ngIf="f.submitted && !appointment.age" class="text-danger">
        Age is required.
      </div>
    </div>

    <!-- Symptoms -->
    <div class="form-group">
      <label for="symptoms">Symptoms:</label>
      <input
        type="text"
        id="symptoms"
        name="symptoms"
        class="form-control"
        placeholder="Enter symptoms"
        required
        [(ngModel)]="appointment.symtomps">
      <div *ngIf="f.submitted && !appointment.symtomps" class="text-danger">
        Symptoms are required.
      </div>
    </div>

    <!-- Number -->
    <div class="form-group">
      <label for="number">Number:</label>
      <input
        type="text"
        id="number"
        name="number"
        class="form-control"
        placeholder="Enter contact number"
        required
        [(ngModel)]="appointment.number">
      <div *ngIf="f.submitted && !appointment.number" class="text-danger">
        Number is required.
      </div>
    </div>

    <!-- Submit button -->
    <button type="submit" class="btn btn-primary mt-3" []="f.invalid">
      Submit
    </button>

  </form>
</div>
