import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Patient } from '../patient';
import { PatientService } from '../patient.service';

@Component({
  selector: 'app-view-patient',
  templateUrl: './view-patient.component.html',
  styleUrls: ['./view-patient.component.css']
})
export class ViewPatientComponent implements OnInit {

  id!: number;
  patient?: Patient;
  loading = true;
  errorMessage = '';

  constructor(
    private patientService: PatientService,
    private route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.id = Number(this.route.snapshot.paramMap.get('id'));
    this.getPatient();
  }

  getPatient(): void {
    this.patientService.getPatientById(this.id).subscribe({
      next: (data) => {
        this.patient = data;
        this.loading = false;
      },
      error: () => {
        this.loading = false;
        this.errorMessage = 'Unable to load patient details.';
      }
    });
  }
  logout(){
    // Implement logout logic if needed
  }
}
<nav class="navbar navbar-expand-lg navbar-dark bg-dark">
  <a class="navbar-brand" href="#">SIDDHINATH HOSPITALS</a>

  <div class="collapse navbar-collapse">
    <div class="ms-auto d-flex align-items-center">
      <button class="btn btn-outline-light me-2" routerLink="/docdash">
        ← Back
      </button>
      <button class="btn btn-danger" (click)="logout()">Logout</button>
    </div>
  </div>
</nav>

<div class="container mt-4">
  <div class="row justify-content-center">
    <div class="col-lg-8 col-md-10">

      <div *ngIf="loading" class="alert alert-info text-center">
        Loading patient details...
      </div>

      <div *ngIf="errorMessage" class="alert alert-danger text-center">
        {{ errorMessage }}
      </div>

     <div *ngIf="patient && !loading" class="card shadow-lg">
  <div class="card-header bg-primary text-white text-center">
    <h4>Patient Details</h4>
  </div>

  <div class="card-body">
    <p><strong>ID:</strong> {{ patient.id }}</p>
    <p><strong>Name:</strong> {{ patient.name }}</p>
    <p><strong>Age:</strong> {{ patient.age }}</p>
    <p><strong>Blood:</strong> {{ patient.blood }}</p>
    <p><strong>Symptoms:</strong> {{ patient.dose}}</p>
    <p><strong>Diagnosis:</strong> {{ patient.prescription }}</p>
    <p></p>
    <p><strong>Urgency:</strong> {{ patient.urgency }}</p>
    <p><strong>Fees:</strong> ₹{{ patient.fees }}</p>
  </div>
</div>


    </div>
  </div>
</div>
