import { Component, OnInit } from '@angular/core';
import { Medicine } from '../medicine';
import { MedicineService } from '../medicine.service';

@Component({
  selector: 'app-medicinelist',
  templateUrl: './medicinelist.component.html',
  styleUrls: ['./medicinelist.component.css']
})
export class MedicinelistComponent implements OnInit {

  medicines: Medicine[] = [];

  constructor(private medicineService: MedicineService) { }

  ngOnInit(): void {
    this.getMedicine();
  }

  getMedicine(): void {
    this.medicineService.getMedicines().subscribe({
      next: (data) => {
        console.log(data);
        this.medicines = data;
      },
      error: (error) => {
        console.error('Error fetching medicines', error);
      }
    });
  }
}

<nav style="margin:auto" class="navbar navbar-expand-lg bg-dark">

    <a style="color: white;" class="navbar-brand" href="#">SIDDHINATH HOSPITALS</a>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarSupportedContent">
      <ul class="navbar-nav me-auto mb-2 mb-lg-0">



        <li class="nav-item">
            <a style="color: white;"class="nav-link active" aria-current="page" href="javascript:void(0);" routerLink="/create-medicine">Add Medicines </a>
          </li>
        <li class="nav-item">
            <a style="color: white;"class="nav-link active" aria-current="page" href="javascript:void(0);" routerLink="/docdash">Back</a>
          </li>

      </ul>

      <div class="mx-2">

        <button style="margin: 5px;"class="btn btn-danger" data-toggle="modal" data-target="#loginModal"><a style="color:white;" href="javascript:void(0);" routerLink="/home">Logout</a></button>

    </div>

    </div>

  </nav>

  <div class="container">
  <hr>
    <h2 style="text-align: center;margin-top: 20PX;">Medicine List</h2>
