  <br> -->
    <a style="color: rgb(86, 86, 86); font-size: xx-large;">Welcome to Medical List Page Click To Add Medicines</a>
<div class="tableFixHead">
  <br>

<div class="container mt-4">

<table class="table table-bordered table-hover table-striped">
    <thead class="table-dark text-center">
    <tr>
        <th>Id</th>
        <th>Drug Name</th>
        <th>Stock</th>
        <th> Action</th>
    </tr>
    </thead>

    <tbody class="text-center">
      <tr *ngFor="let medicine of medicines">
        <td>{{ medicine.id }}</td>
        <td>{{ medicine.drugName }}</td>
        <td>
        <span 
            [ngClass]="medicine.stock">
            {{ medicine.stock }}
        </span>
        </td>
<td>

          <button (click) = "medicine(medicine.id)" class="btn btn-info" style="margin-left: 10px;">Update</button> 

          <button (click) = "deletePatient(patient.id)" class="btn btn-danger" style="margin-left: 10px;">Delete</button> 



        </td>        </tr>
    </tbody>
</table>

  <div *ngIf="medicines.length === 0" class="alert alert-warning text-center">
    No medicines available
    </div>
</div>
