import { Component, OnInit } from '@angular/core';
import { Patient } from '../patient';
import { ActivatedRoute, Router } from '@angular/router';
import { PatientService } from '../patient.service';

@Component({
  selector: 'app-update-patient',
  templateUrl: './update-patient.component.html',
  styleUrls: ['./update-patient.component.css']
})
export class UpdatePatientComponent implements OnInit {

  patient: Patient = new Patient();

  constructor(
    private route: ActivatedRoute,
    private patientService: PatientService,
    private router: Router
  ) {}

  ngOnInit(): void {
    const id = this.route.snapshot.params['id'];

    this.patientService.getPatientById(id).subscribe({
      next: (data) => {
        this.patient = data;
      },
      error: (error) => {
        console.error('Error fetching patient', error);
      }
    });
  }

  onSubmit() {
  const id = this.patient.id;

  this.patientService.updatePatient(id, this.patient).subscribe({
    next: (data) => {
      console.log('Updated:', data);
      this.goToDocDash();
    },
    error: (error) => {
      console.error('Update failed', error);
    }
  });
}


  goToDocDash() {
    this.router.navigate(['/docdash']);
  }
}


<div class="container mt-4">
  <h2 class="text-center mb-4">Update Patient</h2>

  <form #f="ngForm" (ngSubmit)="onSubmit()" novalidate>

    <!-- Name -->
    <div class="form-group mb-3">
      <label>Name</label>
      <input
        type="text"
        class="form-control"
        name="name"
        [(ngModel)]="patient.name"
        required>
    </div>

    <!-- Age -->
    <div class="form-group mb-3">
      <label>Age</label>
      <input
        type="text"
        class="form-control"
        name="age"
        [(ngModel)]="patient.age"
        required>
    </div>

    <!-- Blood Group -->
    <div class="form-group mb-3">
      <label>Blood Group</label>
      <input
        type="text"
        class="form-control"
        name="blood"
        [(ngModel)]="patient.blood"
        required>
    </div>

    <!-- Prescription -->
    <div class="form-group mb-3">
      <label>Prescription</label>
      <input
        type="text"
        class="form-control"
        name="prescription"
        [(ngModel)]="patient.prescription"
        required>
    </div>

    <!-- Dose -->
    <div class="form-group mb-3">
      <label>Dose</label>
      <input
        type="text"
        class="form-control"
        name="dose"
        [(ngModel)]="patient.dose"
        required>
    </div>

    <!-- Fees -->
    <div class="form-group mb-3">
      <label>Fees</label>
      <input
        type="text"
        class="form-control"
        name="fees"
        [(ngModel)]="patient.fees"
        required>
    </div>

    <!-- Urgency -->
    <div class="form-group mb-4">
      <label>Urgency</label>
      <input
        type="text"
        class="form-control"
        name="urgency"
        [(ngModel)]="patient.urgency"
        required>
    </div>

    <!-- Submit -->
    <button
      type="submit"
      class="btn btn-primary"
      [disabled]="f.invalid">
      Update Patient
    </button>

  </form>
</div>
