import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root',
})
export class AuthService {
  private baseUrl = 'http://localhost:8080/api/login';

  constructor(private http: HttpClient) {}

  authenticate(username: string, password: string): Observable<any> {
    return this.http.post(this.baseUrl, { username, password });
  }

  authenticate(username2: string, password: string) {
    if (username2 === 'doctor' && password === 'doc@123') {
      sessionStorage.setItem('username2', username2);
      return true;
    } else {
      return false;
    }
  }

  isUserLogedIn(){
    console.log('Doctor Login ho gaya hai');
    let user = sessionStorage.getItem('username2');
    return !(user == null);
  }

  logout(){
    console.log('Doctor logout ho gaya hai');
    sessionStorage.removeItem('username2');
  }

}
