import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html'
})
export class LoginComponent {

  username: string = '';
  password: string = '';
  errorMessage: string = '';

  constructor(private loginService:LoginComponent, private router: Router) {}

  login() {
    this.authService.login(this.username, this.password).subscribe({
      next: (res: any) => {
        if (res.role === 'ADMIN') {
          this.router.navigate(['/admindash']);
        } else if (res.role === 'DOCTOR') {
          this.router.navigate(['/docdash']);
        }
      },
      error: () => {
        this.errorMessage = 'Invalid username or password';
      }
    });
  }
}
<div class="login-container">
  <h2>Login</h2>
  <div *ngIf="errorMessage" class="alert alert-danger">{{ errorMessage }}</div>

  <input type="text" [(ngModel)]="username" placeholder="Username" class="form-control mb-2" />
  <input type="password" [(ngModel)]="password" placeholder="Password" class="form-control mb-2" />

  <button class="btn btn-primary" (click)="login()">Login</button>
</div>
