import { Component, OnInit } from '@angular/core';
import { Medicine } from '../medicine';
import { MedicineService } from '../medicine.service';

@Component({
  selector: 'app-medicinelist',
  templateUrl: './medicinelist.component.html',
  styleUrls: ['./medicinelist.component.css']
})
export class MedicinelistComponent implements OnInit {

  medicines: Medicine[] = [];

  constructor(private medicineService: MedicineService) { }

  ngOnInit(): void {
    this.getMedicine();
  }

  getMedicine(): void {
    this.medicineService.getMedicines().subscribe({
      next: (data) => {
        console.log(data);
        this.medicines = data;
      },
      error: (error) => {
        console.error('Error fetching medicines', error);
      }
    });
  }
}
<div class="container mt-4">
  <h3 class="text-center mb-3">Medicine List</h3>

  <table class="table table-bordered table-hover table-striped">
    <thead class="table-dark text-center">
      <tr>
        <th>Id</th>
        <th>Drug Name</th>
        <th>Stock</th>
      </tr>
    </thead>

    <tbody class="text-center">
      <tr *ngFor="let medicine of medicines">
        <td>{{ medicine.id }}</td>
        <td>{{ medicine.drugName }}</td>
        <td>
          <span 
            class="badge"
            [ngClass]="medicine.stock > 0 ? 'bg-success' : 'bg-danger'">
            {{ medicine.stock }}
          </span>
        </td>
      </tr>
    </tbody>
  </table>

  <!-- Optional: No data message -->
  <div *ngIf="medicines.length === 0" class="alert alert-warning text-center">
    No medicines available
  </div>
</div>
